INSERT INTO `subcategoria` (`ID_SUBCAT`, `ID_CAT`, `N_SUBCAT`, `SUBCATDESC`, `SUBCATIMG`, `FOTO`) VALUES
(1, 1, 'Bordados en Gorras', 'Ofrecemos gorras bordadas de excelente calidad y totalmente a tu gusto. En Bordintex pensamos siempre  en los detalles, lo que significa personalizado. Ven con nosotros y diseñemos una gorra. ¡Te esperamos!', 'http://www.bordintex.com/public/subcategorias/bordadosGorras.png', '317e0-bordadosgorras.png'),
(2, 1, 'Bordados en Camisetas', 'Ofrecemos Bordados para todo tipo de camisas, para instituciones educativas, empresas, equipos de futbol, etc. Ven con nosotros y diseñemos una camiseta. ¡Te esperamos!', 'http://www.bordintex.com/public/subcategorias/bordadosCamisetas.png', '353fc-bordadoscamisetas.png'),
(3, 1, 'Bordados de Nombres', 'Nuestro nombre es nuestro legado y llevarlo bien en tu prenda de vestir lo hacemos nosotros, Ven con nosotros bordemos y diseñemos tu nombre en cualquier tipo de prenda o donde tú prefieras. ¡Te esperamos!', 'http://www.bordintex.com/public/subcategorias/bordadosNombres.png', '68338-bordadosnombres.png'),
(4, 1, 'Bordados en Chalecos', 'Uff! ¡Sabemos que en quito hace frio, y valla que mucho! No te preocupes, nosotros podemos diseñarte o realizar un super Bordado para que pongas tú marca personal. Ven con nosotros bordemos o diseñemos un chaleco. ¡Te esperamos!', 'http://www.bordintex.com/public/subcategorias/bordadoschalecos.png', '2a710-bordadoschalecos.png'),
(5, 1, 'Bordados en Chompas', 'Uff! ¡Sabemos que en quito hace frio, y valla que mucho! No te preocupes, nosotros podemos diseñarte o realizar un super Bordado para que pongas tú marca personal. Ven con nosotros bordemos o diseñemos una chompa. ¡Te esperamos!', 'http://www.bordintex.com/assets/uploads/files/c8ec7-chompa.png', 'c8ec7-chompa.png'),
(6, 1, 'Bordados Personalizados', '¡Personaliza cualquier idea en tu prenda de vestir o donde tú quieras! Ofrecemos asociaría y digitalización de tu imagen o diseño.', 'http://www.bordintex.com/public/subcategorias/bordadosPersonalizados.png', '7ea5e-bordadospersonalizados.png'),
(8, 1, 'Almohadas personalizadas', 'disponibles en varios tamaños', 'http://www.bordintex.com/assets/uploads/files/88daa-531aa-almohada.jpg', '88daa-531aa-almohada.jpg');
